import React from "react";
import { StyledCostFooter } from "./styles/CostFooter.styled";

function CostFooter({ watchFields, fields }) {

  const orderTotalPrice = fields && fields.reduce((acc, val) => {
    acc[val.pricing.currencyUnit] = (acc[val.pricing.currencyUnit] || 0) + parseInt(val.pricing.totalCost);
    return acc;
  }, {});

  console.log(orderTotalPrice)

  return (
    <StyledCostFooter>
      <div>TOPLAM TUTAR:</div>
      {/* <div>{fields.map((acc,cur) => {return acc.concat(cur.pricing.totalCost," ",cur.pricing.currencyUnit," + ")},[])}</div> */}

      {/* {fields.map(
        (item, index) => 
            (
            <div key={index}>
              <div>
                {item.pricing.totalCost}
                {` ${item.pricing.currencyUnit}`}
              </div>
              <div>{fields[index + 1]?.pricing.currencyUnit && `+`}</div>
            </div>
          )
      )} */}
      {/* {fields.reduce((fullCost, loop_item) => (
        <>
          <div>{loop_item.pricing.currencyUnit}</div>
          <div>
            {fields.map(
              (item) =>
                loop_item.pricing.currencyUnit === item.pricing.currencyUnit
            ) && fullCost + loop_item.pricing.cost}
          </div>
        </>
      ))} */}
      {orderTotalPrice && 
        <>
          <div>{Object.keys(orderTotalPrice).filter((item) => item && item !== 'undefined').map((key, index) => {
            return (
                  <div key={index} className='mr-2'>
                    <div>{orderTotalPrice[key]} {key}</div>
                  </div>
            )
          })}</div>
        </>
      }
    </StyledCostFooter>
  );
}
export default CostFooter;
